// Simple blockchain structure in JavaScript
class Block {
  constructor(index, data, previousHash) {
    this.index = index;
    this.data = data;
    this.previousHash = previousHash;
  }
}
const genesisBlock = new Block(0, 'Genesis Block', '0');
console.log(genesisBlock);

// Example of a simple Proof-of-Stake function
function validateStake(stake, totalSupply) {
  return stake / totalSupply;
}
console.log(validateStake(100, 1000)); // 0.1

// Solidity smart contract example
pragma solidity ^0.8.0;
contract HelloSonic {
    string public message;
    constructor() {
        message = 'Hello, Sonic!';
    }
    function setMessage(string memory newMessage) public {
        message = newMessage;
    }
}

// Solidity Re-Entrancy Guard
pragma solidity ^0.8.0;
contract SecureContract {
    bool private locked;
    modifier noReentrant() {
        require(!locked, 'No re-entrancy');
        locked = true;
        _;
        locked = false;
    }
}
